mapper.select:
SELECT COUNT(likeno) as cnt
FROM liketo
WHERE contentsno=#{contentsno} AND memberno=#{memberno}

mapper.update:
UPDATE liketo
SET like_check = like_check + 1 
WHERE memberno=#{memberno} AND contentsno=#{contentsno}

UPDATE liketo
SET like_check = 0
WHERE memberno=#{memberno} AND contentsno=#{contentsno}

mapper.select:
SELECT likeno, contentsno, memberno, like_check 
FROM liketo
WHERE contentsno=#{contentsno} AND memberno=#{memberno}
